rrList5.add("Black");				System.out.println("Original ArrayList :");		System.out.println(arrList5);				System.out.println("ArrayList after reverse :");		Collections.reverse(arrList5);		System.out.println(arrList5);		System.out.println();				//Write a Java program of swap two elements in an array list.		ArrayList<String> arrList6 = new ArrayList<String>();		arrList6.add("Yellow");		arrList6.add("Blue");		arrList6.add("Brown");		arrList6.add("Black");				System.out.println("Original ArrayList :");		System.out.println(arrList6);				System.out.println("ArrayList after swap position of 1 & 3 :");		Collections.swap(arrList6,1,3);		System.out.println(arrList6);		System.out.println();				//Write a Java program to print all the elements of a ArrayList using the position of the elements.		ArrayList<String> arrList7 = new ArrayList<String>();		arrList7.add("Yellow");		arrList7.add("Blue");		arrList7.add("Brown");		arrList7.add("Black");				System.out.println("Printing arraylist using position");		for(String s :arrList7){		//Q7{			System.out.print(s+" ");		}		System.out.println();	}}