/*	Note: a palindrome is a sequence of characters that reads the same forwards and backwards.	Ex: Given the following strings...	"A man, a plan, a canal: Panama.", return true*/import java.util.*;class PalindromeChecker{		static boolean isPalindrome(String str){		int size = str.length();		String str1 = str.toLowerCase();		int left = 0;		int right = size-1;		while(right >= left){			if(str1.charAt(right) == str1.charAt(left)){				right--;				left++;			}			else if(!Character.isLetter(str1.charAt(right))) {				right--;			}			else if(!Character.isLetter(str1.charAt(left))){				left++;			} 			else{				return false;			}		}		return true;	}		public static void main(String args[]){		/*		Scanner sc = new Scanner(System.in);		String str = sc.nextLine();		*/		String str = "A man, a plan, a canal: Panama";		boolean b = isPalindrome(str);				if(b){			System.out.println(b);		}		else{			System.out.println(b);		}	}}