/*	Given a string representing your stones and another string representing a list of jewels, return the number of stones that you have that are also jewels.	Ex: Given the following jewels and stones...		jewels = "abc", stones = "ac", return 2		jewels = "Af", stones = "AaaddfFf", return 3		jewels = "AYOPD", stones = "ayopd", return 0*/class JewelsAndStones{	static void findNumberOfJewelsInStones(String jewels, String stones){		int count = 0;		for(int i=0; i<jewels.length(); i++){			for(int j=0; j<stones.length(); j++){				if(jewels.charAt(i)==stones.charAt(j)){					count++;				}			}		}		System.out.println(count);	}		public static void main(String args[]){		String jewels1 = "abc";		String stones1 = "ac";		System.out.println("Jewels : "+jewels1+" ; stones : "+stones1);		findNumberOfJewelsInStones(jewels1,stones1);		System.out.println();				String jewels2 = "Af";		String stones2 = "AaaddfFf";		System.out.println("Jewels : "+jewels2+" ; stones : "+stones2);		findNumberOfJewelsInStones(jewels2,stones2);		System.out.println();				String jewels3 = "AYOPD";		String stones3 = "ayopd";		System.out.println("Jewels : "+jewels3+" ; stones : "+stones3);		findNumberOfJewelsInStones(jewels3,stones3);		System.out.println();	}}