/*	Given two strings, s and t, merge the two strings together alternating characters starting with s.	Note: If one string is longer than the other, append the remaining characters of the longer string at the end of the merged string.	s = "abc", t = "def", return "adbecf".*/class MergeTwoStrings{	static void mergeTwoString(String s1, String s2){		int n1 = s1.length();		int n2 = s2.length();		char s[] = new char[n1+n2];		int j = 0;		for(int i=0; i<n1; i++){			s[j++] = s1.charAt(i);			s[j++] = s2.charAt(i);		}				System.out.print("Resultant merge String : ");		for(int i=0; i<s.length; i++){			System.out.print(s[i]);		}	}		public static void main(String args[]){		String s1 = "abc";		String s2 = "def";		System.out.println("String 1 : "+s1);		System.out.println("String 2 : "+s2);		mergeTwoString(s1,s2);	}}