/*	This question is asked by Google. Given a string, return whether or not it uses capitalization correctly. A string correctly uses capitalization if all letters are capitalized, no letters are capitalized, or only the first letter is capitalized.	Ex: Given the following strings...	"USA", return true	"Calvin", return true	"compUter", return false	"coding", return true*/import java.util.*;class CorrectWord{		static void isCorrectWord(String str){		int flag1 = 0;		int flag2 = 0;		if(Character.isUpperCase(str.charAt(0))){			for(int i=1; i<str.length(); i++){				if(Character.isUpperCase(str.charAt(i)))					flag1++;				else					flag2++;			}			if(flag1 == 0 || flag2 == 0)				System.out.println(true);			else				System.out.println(false);		}		else{			for(int i=1; i<str.length(); i++){				if(!Character.isLowerCase(str.charAt(i))){					System.out.println(false);					return;				}			}			System.out.println(true);		}	}		public static void main(String args[]){		Scanner sc = new Scanner(System.in);		String str = sc.nextLine();				isCorrectWord(str);				/*		String str1 = "USA";		String str2 = "Calvin";		String str3 = "compUter";		String str4 = "coding";				isCorrectWord(str1);		isCorrectWord(str2);		isCorrectWord(str3);		isCorrectWord(str4);		*/			}}