/*	This question is asked by Amazon. Given a string representing the sequence of str a robot vacuum makes, return whether or not it will return to its original position. The string will only contain L, R, U, and D characters, representing left, right, up, and down respectively.	Ex: Given the following strings...	"LR", return true	"URURD", return false	"RUULLDRD", return true*/import java.util.*;class SequenceOfMoves{		static boolean movesSequence(String str){        if(str == null || str.length() == 0)            return true;        int x = 0;        int y = 0;        for(int i = 0; i < str.length(); i++){            char c = str.charAt(i);            if(c == 'R'){                x++;            } 			else if(c == 'L'){                x--;            } 			else if(c == 'U'){                y++;            } 			else if(c == 'D'){                y--;            }        }        return (x == 0 && y == 0);    }		public static void main(String args[]){		Scanner sc = new Scanner(System.in);		String str = sc.nextLine();		boolean b = movesSequence(str);		System.out.println(b);				/*		String str1 = "LR";		String str2 = "URURD";		String str3 = "RUULLDRD";		boolean b1 = movesSequence(str1);		boolean b2 = movesSequence(str2);		boolean b3 = movesSequence(str3);		System.out.println(b1);		System.out.println(b2);		System.out.println(b3);		*/	}}