/*	Given two binary strings (strings containing only 1s and 0s) return their sum (also as a binary string).	Note: neither binary string will contain leading 0s unless the string itself is 0	Ex: Given the following binary strings...	"100" + "1", return "101"	"11" + "1", return "100"	"1" + "0", return "1"*/import java.util.*;class BinarySum{	public static void main(String args[]){		Scanner sc= new Scanner(System.in);		System.out.println("Enter 1st binary numbers");		String b1= sc.next();		System.out.println("Enter 1st binary numbers");		String b2= sc.next();				int n1=Integer.parseInt(b1, 2);		int n2=Integer.parseInt(b2, 2);				int n3=n1+n2;				System.out.println("1st Binary number= "+Integer.toBinaryString(n1));				System.out.println("2nd Binary number= "+Integer.toBinaryString(n2));				System.out.println("Addition of two Binary numbers = "+Integer.toBinaryString(n3));	}}