import java.util.*;class Circle{		static double Circumference(int rad){		double Circum = 2 * 3.145 * rad;		return Circum;	}		static double Area(int rad){		double Area = 3.145 * rad * rad;		return Area;	}		public static void main(String args[]){				Scanner sc = new Scanner(System.in);		System.out.println("Enter the radius ");		int radius = sc.nextInt();				System.out.println("Circumference of circle is "+Circumference(radius));				System.out.println("Area of circle is "+Area(radius));			}}