import java.util.*;class SubjectMarksAndPercentage{	public static void main(String args[]){				Scanner sc = new Scanner(System.in);		System.out.println("Enter the marks for Subject1:");		int a = sc.nextInt();		System.out.println("Enter the marks for Subject2:");		int b = sc.nextInt();		System.out.println("Enter the marks for Subject3:");		int c = sc.nextInt();		System.out.println("Enter the marks for Subject4:");		int d = sc.nextInt();		System.out.println("Enter the marks for Subject5:");		int e = sc.nextInt();				int sum = a+b+c+d+e;		double avg = sum / 5;				System.out.println("[ Percentage marks = "+avg+" % ]");			}}