import java.util.*;class SimpleInterest{	public static void main(String args[]){		Scanner sc = new Scanner(System.in);				System.out.println("Enter the Principle amount ");		int P = sc.nextInt();		System.out.println("Enter the Rate of Interest ");		double R = sc.nextDouble();		System.out.println("Enter the Time Period ");		double N = sc.nextDouble();				double SI = (P * R * N) / 100;				System.out.println("Simple Interest is "+SI);	}}