import java.util.*;class DaysToYearMonthDays{	public static void main(String args[]){				Scanner sc = new Scanner(System.in);		System.out.println("Enter the number of days ");		int d = sc.nextInt();		int y = 0;		int m = 0;		int day = 0;		while (d>365){			y++;			d-=365;		}		while(d>30){			m++;			d-=30;		}		while(d>0){			day++;			d-=1;		}		System.out.println("year "+y+" month "+m+" days "+day);	}}