class Room{	int height;	int width;	int breadth;		Room(){		height=0;		width=0;		breadth=0;	}		Room(int h, int w, int b){		height=h;		width=w;		breadth=b;	}		int volume(){		int vol = height * width * breadth;		return vol;	}}class RoomDemo{	public static void main(String args[]){		Room r = new Room(10,30,40);		System.out.println("Volume of room : "+r.volume());	}	}