import java.lang.*;import java.util.*;class MathOperation{		static int add(int a, int b){		int sum = a+b;		return sum;	}	static int subtract(int a, int b){		int difference = a-b;		return difference;	}	static int multiply(int a, int b){		int product = a*b;		return product;	}	static int power(int a, int b){		int power = (int)(Math.pow(a,b));		return power;	}}class Demo{	public static void main(String args[]){		Scanner sc = new Scanner(System.in);		System.out.println("Enter 1st number: ");		int a = sc.nextInt();		System.out.println("Enter 2nd number: ");		int b = sc.nextInt();				int sum = MathOperation.add(a,b);		int difference = MathOperation.subtract(a,b);		int product = MathOperation.multiply(a,b);		int power = MathOperation.power(a,b);				System.out.println("Sum of "+a+" and "+b+" is "+sum);		System.out.println("Difference of "+a+" and "+b+" is "+difference);		System.out.println("Product of "+a+" and "+b+" is "+product);		System.out.println("power of "+a+" to "+b+" is "+power);			}}