class Account{	long accountNumber;	String holderName;	double balance;		Account(){		accountNumber=0;		holderName="noHolder";		balance=0;	}		Account(long accountNumber, String holderName, double balance){		this.accountNumber = accountNumber;		this.holderName = holderName;		this.balance = balance;	}			void printDetails() {        System.out.println("Account Number : "+accountNumber);        System.out.println("Account holder Name : "+holderName);        System.out.println("Account Balance : "+balance);    }}class SavingAccount extends Account{	double interestRate=3.0;		double calculateyearlyInterest(){		return balance * interestRate;	}		SavingAccount(){			}		SavingAccount(long accountNumber, String holderName, double balance){		super(accountNumber,holderName,balance);	}		void printDetails()    {        super.printDetails();        System.out.println("Interest rate : "+interestRate);		System.out.println("Account type : Saving");		System.out.println("***************************");    }}class CurrentAccount extends Account{	CurrentAccount(){			}		CurrentAccount(long accountNumber, String holderName, double balance){		super(accountNumber,holderName,balance);	}		void printDetails(){        super.printDetails();		System.out.println("Account type : Current");		System.out.println("***************************");    }}class Manager{	Account acc[] = new Account[1];	    	Manager(){        acc[0] = new Account();    }    void add(Account x){        int n = acc.length;        Account temp[] = new Account[n + 1];        for (int i = 0; i < n; i++)            temp[i] = acc[i];        temp[n] = x;        acc = temp;    }    void printDetails(){        for (int i = 0; i < acc.length; i++){            acc[i].printDetails();        }    }		public static void main(String args[]){		Manager m = new Manager();				SavingAccount s1 = new SavingAccount(123,"asdf",456.66);		SavingAccount s2 = new SavingAccount();				CurrentAccount c1 = new CurrentAccount(889,"ert",45.99);		CurrentAccount c2 = new CurrentAccount();		CurrentAccount c3 = new CurrentAccount();				m.add(s1);		m.add(s2);		m.add(c1);		m.add(c2);		m.add(c3);				s1.printDetails();		s2.printDetails();		c1.printDetails();		c2.printDetails();		c3.printDetails();	}}