interface Shape{	void area();		void draw();		void rotate();		void move(int a, int b);}class Circle implements Shape{		int radius;		Circle(){		this.radius = 0;	}		Circle(int radius){		this.radius = radius;	}		public void area(){		double area = 3.14 * radius * radius;		System.out.println("Area of circle having radius "+this.radius+" is "+area);	}		public void move(int a, int b){		System.out.println("Circle move : "+a+" and "+b);	}		public void draw(){			}		public void rotate(){			}		void printDetails(){		System.out.println("Radius of Circle : "+radius);	}}class Rectangle implements Shape{		int length, breadth;		Rectangle(){		this.length = 0;		this.breadth = 0;	}		Rectangle(int length, int breadth){		this.length = length;		this.breadth = breadth;	}		public void area(){		int area = length * breadth;		System.out.println("Area of Rectangle having length "+this.length+" and breadth "+this.breadth+" is "+area);	}		public void move(int a, int b){		System.out.println("Rectangle move : "+a+" and "+b);	}		public void draw(){			}		public void rotate(){			}		void printDetails(){		System.out.println("Length of Rectangle : "+length);		System.out.println("Breadth of Rectangle : "+breadth);	}}class ShapeDemo{	public static void main(String args[]){		Circle c1 = new Circle(2);		Circle c2 = new Circle(4);				Rectangle r1 = new Rectangle(1,2);		Rectangle r2 = new Rectangle(2,3);		Rectangle r3 = new Rectangle(3,4);				c1.area();		c2.area();				r1.area();		r2.area();		r3.area();				c1.move(2,3);		r1.move(4,5);				c1.printDetails();		r1.printDetails();			}}