class ComplexNumber{	int real;	int imaginary;		ComplexNumber(){		real=0;		imaginary=0;	}		ComplexNumber(int r){		real=r;		imaginary=0;	}		ComplexNumber(int r, int i){		real=r;		imaginary=i;	}		ComplexNumber add(ComplexNumber c){		ComplexNumber t = new ComplexNumber();		t.real = real + c.real;		t.imaginary = imaginary + c.imaginary;		return t;	}		ComplexNumber multiply(ComplexNumber c){		ComplexNumber t = new ComplexNumber();		t.real = real * c.real;		t.imaginary = imaginary * c.imaginary;		return t;	}		void print(){		System.out.println(real+"+("+imaginary+")i");	}		public static void main(String args[]){		ComplexNumber c1 = new ComplexNumber(3,2);		ComplexNumber c2 = new ComplexNumber(4,-2);				ComplexNumber c3 = c1.add(c2);		System.out.println("Sum of complex number : ");		c3.print();				c3 = c1.multiply(c2);		System.out.println("Product of complex number : ");		c3.print();			}}