class Student{	String nameOfStudent;	int marks[] = new int[3];		Student(){		nameOfStudent = "NuLL";		marks[0] = 0;		marks[1] = 0;		marks[2] = 0;	}		Student(String name, int m1, int m2, int m3){		nameOfStudent = name;		marks[0] = m1;		marks[1] = m2;		marks[2] = m3;	}		void average(){		int sum = 0;		for(int i=0; i<3; i++){			sum+=marks[i];		}		float avg = (float)sum/3;		System.out.println("Average of marks : "+avg);	}		void display(){		System.out.println("Name of student : "+nameOfStudent);		for (int i=0; i<3; i++){			System.out.println("Marks of subject "+(i+1)+" : "+marks[i]);		}	}			public static void main(String args[]){		Student stud1 = new Student("Ashish",40,55,30);				stud1.display();		stud1.average();			}	}